import { useState, useEffect } from 'react';
export default function useDirection() {
  const {
    0: direction,
    1: setDirection
  } = useState('ltr');
  useEffect(() => {
    const body = document.getElementsByTagName('body')[0];
    const bodyDir = body.getAttribute('dir');
    setDirection(bodyDir);
  }, []);
  return [direction];
}
